module.exports = class Author {
  /** Construct a new Author from either a full name or a JSON object. The object
  constructor is intended for deserialization. */
  constructor(o) {
    switch (typeof o) {
      case 'string': {
        this.fullName = o;
      } break;

      case 'object': {
        Object.assign(this, o);
      } break;
    }
  }
}
