const path = require('path');

const log = require('log');
const neo = require('neo');

const $ = require('jquery');

var StoryEditor = document.registerElement('story-editor');
var NeoStory = document.registerElement('neo-story');
var NeoTitle = document.registerElement('neo-title');

self = module.exports = {
  get StoryFolders() {
    return [path.join(__dirname, 'docs')];
  },

  get name() {
    return 'story';
  },

  openTab() {
    var workDiv = $(neo.StoryPane).find('.work');
    $(workDiv).html('<story-editor>' + neo.CurrentStory.story.body + '</story-editor>');
  }
}

function initTab() {
  var cssLink = $('<link type="text/css" rel="stylesheet" href="' + path.join(__dirname, 'style.css') + '"></link>');
  $('head').append(cssLink);
};

initTab();
